$(function(){
  var interval_delay = 100;
  var original_top = $('.main').css('top');
  var top_position = parseInt(original_top); 

  var interval_id = window.setInterval( function(){
      top_position += 1;
      $('.main').css('top', top_position);
  }, interval_delay);

});

